import React, { useRef } from 'react';
import mEye from "../../assets/mortyEye.png"
// import rEye from "../../assets/RickEye.png"
import morty from "../../assets/morty.png"
import { useEyeMovement } from "../Eyes/useEyeMovement.js"


const About = () => {


    const eyeLeftRef = useRef(null);
    const eyeRightRef = useRef(null);
    const anchorRef = useRef(null);

    useEyeMovement(eyeLeftRef, eyeRightRef, anchorRef, 64, 56, 330);




    return (
        <div className={style.backgroundImage}>
        <>
                <img id='morty' ref={anchorRef} src={morty} alt="<Morty Anchor>" style={{  width: '336px', height: '514px', position: 'fixed', bottom:'-10px', left: '-20px' }} />
                <div id="mEye">
                    <img ref={eyeLeftRef} src={mEye} alt="left eye" style={{ position: 'fixed', width: '9.5px', height: '9px', top: '-43px', left: '-18px' }} />
                    <img ref={eyeRightRef} src={mEye} alt="right eye" style={{ position: 'fixed', width: '9.5px', height: '9px', top: '-68px', left: '98px'  }} />
                </div>


            <h2 className={style.title}>About me...</h2>
            <p className={style.text}>Hello! My name is Pablo, and I am  passionate about technology and currently studying JavaScript, React and Express. 
            I am 36 years old and currently looking to change careers into the tech industry. 
            I reside in Mexico and I am bilingual, fluent in both English and Spanish.

I am dedicated to expanding my knowledge and skills in JavaScript and React, as I believe they are essential tools for building modern and interactive web applications. <br>

</br><p></p>I am constantly seeking new challenges and opportunities to grow as a developer.

With my strong determination and enthusiasm, I am confident in my ability to adapt to the ever-evolving tech landscape. I am excited to contribute to the tech industry 
and make a positive impact through my work.

If you have any questions or need assistance, feel free to reach out to me. I am always eager to collaborate and help others in their learning journey.

Happy coding!</p>
        
        </>
        </div>
    )
}

export default About
