import { useState, useEffect, useRef } from 'react'
import { useParams } from 'react-router-dom'
import style from './Detail.module.css'
import 'animate.css/animate.min.css';
import { useNavigate } from 'react-router-dom';


const Detail =() => {
    const { id } = useParams()

    
    const divRef = useRef(null);

    const [ character, setCharacter ] = useState({})

    //CLOSE BUTTON
    const navigate = useNavigate();
    const handleClose = () => {
      divRef.current.classList.add('animate__bounceOut');
      setTimeout(() => navigate(-1), 1000); //Navigate AFTER 1Sec. The delay gives the animation time to complete before the navigation occurs. 

    }

    useEffect(() => {
        fetch(`http://localhost:3001/rickandmorty/character/${id}`)
        // .then es un metodo de promesa
          .then((response) => response.json()) // Nos brinda info de la api y la parseamos
          .then((char) => {
            if (char.name) {
              setCharacter(char);
            } 
            else {
              alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            alert("No hay personajes con ese ID");
          });

        return setCharacter({});
      }, [id]);


    return (


        <div ref={divRef} className="animate__animated animate__bounceIn">
            {
                character ? ( 
                  <a href="#" className="flex flex-col items-center bg-white border border-gray-200 rounded-lg shadow md:flex-row md:max-w-xl hover:bg-gray-100 dark:border-gray-700 dark:bg-gray-800 dark:hover:bg-gray-700">                                
                  <button onClick={handleClose} className={style.closeButton}>Close</button>
                  <img className="object-cover w-full rounded-t-lg h-96 md:h-auto md:w-48 md:rounded-none md:rounded-l-lg" src={character.image} alt={character.name} />
                    <div className="flex flex-col justify-between p-4 leading-normal">
                        <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{character.name}</h5>
                        <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">{character.status}</p>
                        <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">{character.species}</p>
                        <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">{character.gender}</p>
                        <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">{character.origin?.name}</p>
                    </div>
                </a>
            ) : (
                ''
            )
        }
    </div>
)
}

export default Detail
