import React, { useEffect, useRef } from 'react';
import imageLogo from '../../assets/logoNav.png';
import eyes from '../../assets/eye.png';

const EyeSeeYou = () => {
    const eyeRef = useRef(null);
    const anchorRef = useRef(null);
    const eyeLeftRef = useRef(null); // Add this line
    const eyeRightRef = useRef(null); // Add this line

    useEffect(() => {
        if (anchorRef.current) {
            const rekt = anchorRef.current.getBoundingClientRect();
            const anchorX = rekt.left + rekt.width / 2;
            const anchorY = rekt.top + rekt.height / 2;

            const handleMouseMove = (event) => {
                [eyeRef.current].forEach((eye)=> {
                    const mouseX = event.clientX;
                    const mouseY = event.clientY;
                    const angleDeg = angle(mouseX, mouseY, anchorX, anchorY);

                    eyes.forEach(eye => {
                        eye.style.transform = `rotate(${90 + angleDeg}deg)`;
                    });
                });
            }

            function angle(cx, cy, ex, ey) {
                const dy = ey - cy;
                const dx = ex - cx;
                const rad = Math.atan2(dy, dx);
                const deg = rad * 180 / Math.PI;
                return deg;
            }

            document.addEventListener('mousemove', handleMouseMove);

            return () => {
                document.removeEventListener('mousemove', handleMouseMove);
            };
        }
    }, []);

    return (
        <body>
            <main>
                <img id='anchor' ref={anchorRef} src={imageLogo} alt="logo rick and morty" style={{ width: '250px', height: '250px', position: 'fixed', top: '0px', left: '0px' }} />
                <div id="eyes">
                    <img ref={eyeLeftRef} src={eyes} alt="left eye" style={{ position: 'absolute', width: '5px', height: '5px', top: '93px', left: '106px' }} />
                    <img ref={eyeRightRef} src={eyes} alt="right eye" style={{ position: 'absolute', width: '5px', height: '5px', top: '93px', left: '130px' }} />
                </div>
            </main>
        </body>
    )
}

export default EyeSeeYou;
