import './App.css'
import Cards from './components/Cards/Cards.jsx'
//import SearchBar from './components//SearchBar/SearchBar.jsx'
import Nav from './components/Nav/Nav'
import { useState, useEffect, useRef } from 'react'
import axios from 'axios'
import { Routes, Route, useLocation, useNavigate } from 'react-router-dom'
import About from './components/About/About'
import Detail from './components/Detail/Detail'
import Form from './components/Form/Form'
import Favorites from './components/Favorites/Favorites'



function App() {
  const eyeRef = useRef(null);

  useEffect(() => {
    const eyes = eyeRef.current.querySelectorAll('.eye');
    const anchor = eyeRef.current.querySelector('#anchor');
    if (anchor) {
      const rekt = anchor.getBoundingClientRect();
      const anchorX = rekt.left + rekt.width / 2;
      const anchorY = rekt.top + rekt.height / 2;

      function angle(cx, cy, ex, ey) {
        const dy = ey - cy;
        const dx = ex - cx;
        const rad = Math.atan2(dy, dx);
        const deg = rad * 180 / Math.PI;
        return deg;
      }

      function handleMouseMove(e) {
        const mouseX = e.clientX;
        const mouseY = e.clientY;
        const angleDeg = angle(mouseX, mouseY, anchorX, anchorY);

        eyes.forEach(eye => {
          eye.style.transform = `rotate(${90 + angleDeg}deg)`;
        });
      }

      document.addEventListener('mousemove', handleMouseMove);

      // Clean up the event listener when the component unmounts
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
      };
    }
  }, []);


  const [ characters, setCharacters ] = useState([])

  const [ access, setAccess ] = useState(false)



  const location = useLocation()
  const navigate = useNavigate()

  // const login = (userData) => {
  //   if(userData.username === username && userData.password === password  ) {
  //     setAccess(true)
  //     navigate('/home')
  //   }
  // }

  async function login(userData) {
    try {
      const { username, password } = userData;

      const URL = 'http://localhost:3001/rickandmorty/login/';
      const query = `?email=${username}&password=${password}`

      const { data } = await axios(URL + query )
      const { access } = data;

         setAccess(data);
         access && navigate('/home');

    } 

    catch (error) {
      if (error.response && error.response.status === 401) {
        alert('Invalid email or password');
      } else {
        console.error(error);
      }
    }
 }

  useEffect(()=> {
    !access && navigate('/')
  }, [access, navigate])

//Search Fn
  async function onSearch(id) {

    try {

      const endpoint = "http://localhost:3001/rickandmorty/character/" +id
      const { data } = await axios(endpoint)


        if (characters.some(char => char.id === Number(id))) {
          alert(`#${id} is already on the list! :)`)
        } else if (data.id) {
          setCharacters(prevCharacters => [...prevCharacters, data]);
        }


    }
    catch (error){
      console.log('soy el catch', error.message)
      alert('Try with another ID')
    }



  }

 function onClose(id) {
  setCharacters(
    characters.filter((character) => {
      return character.id !== Number(id)
    })
  )
 }

  return (
    <>
    <div id='anchor' className='App' ref={eyeRef}></div>
    <div className='App' ref={eyeRef}>

      {

        location.pathname !== '/' &&  <Nav onSearch= {onSearch} />

      }


        <Routes>

          <Route
            path='/'
            element= { <Form login= {login} /> }

          />

          <Route
            path="/home"
            element={<Cards characters={characters} onClose={onClose}/>}

          />

          <Route 
            path= '/about'
            element={ <About/> }

          />

          <Route 
            path="/detail/:id"
            element={ <Detail/>}

          />

          <Route 
            path='/favorites'
            element= { <Favorites/>}
          />

        </Routes>


    </div>
    </>
  )
}

export default App
