import { useState, useEffect, useRef } from 'react'
import { useParams } from 'react-router-dom'
import style from './Detail.module.css'
import 'animate.css/animate.min.css';
import { useNavigate } from 'react-router-dom';


const Detail =() => {
    const { id } = useParams()

    
    const divRef = useRef(null);

    const [ character, setCharacter ] = useState({})

    //CLOSE BUTTON
    const navigate = useNavigate();
    const handleClose = () => {
      divRef.current.classList.add('animate__bounceOut');
      setTimeout(() => navigate(-1), 1000); //Navigate AFTER 1Sec. The delay gives the animation time to complete before the navigation occurs. 

    }

    useEffect(() => {
        fetch(`http://localhost:3001/rickandmorty/character/${id}`)
        // .then es un metodo de promesa
          .then((response) => response.json()) // Nos brinda info de la api y la parseamos
          .then((char) => {
            if (char.name) {
              setCharacter(char);
            } 
            else {
              alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            alert("No hay personajes con ese ID");
          });

        return setCharacter({});
      }, [id]);

      const closeButton = {
        backgroundColor: 'blue',
        color: 'white',
      }
    return (
        

        <div ref={divRef} className="animate__animated animate__bounceIn">
            {
                character ? ( 
                  <a href="#" style={{display: 'flex', flexDirection: 'column', alignItems: 'center', backgroundColor: 'white', border: '1px solid gray', borderRadius: '0.5rem', boxShadow: '0 0 1rem rgba(0,0,0,0.1)', maxWidth: '40rem'}}>
                  <button onClick={handleClose} style={style.closeButton}>Close</button>
                  <img style={{objectFit: 'cover', width: '100%', borderRadius: '0.5rem 0.5rem 0 0', height: '24rem'}} src={character.image} alt={character.name} />
                  <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'space-between', padding: '1rem', lineHeight: '1.5'}}>
                     <h5 style={{marginBottom: '0.5rem', fontSize: '1.5rem', fontWeight: 'bold', textTransform: 'uppercase', color: '#212121'}}>{character.name}</h5>
                     <p style={{marginBottom: '0.75rem', fontSize: '1rem', color: '#616161'}}>{character.status}</p>
                     <p style={{marginBottom: '0.75rem', fontSize: '1rem', color: '#616161'}}>{character.species}</p>
                     <p style={{marginBottom: '0.75rem', fontSize: '1rem', color: '#616161'}}>{character.gender}</p>
                     <p style={{marginBottom: '0.75rem', fontSize: '1rem', color: '#616161'}}>{character.origin?.name}</p>
    </div>
</a>
            ) : (
                ''
            )
        }
    </div>
)
}

export default Detail
