import style from "./Card.module.css"
import { Link, useLocation } from 'react-router-dom'
import { connect } from "react-redux";
import { addFavorite, deleteFavorite } from "../../redux/actions";
import { useEffect, useState } from "react";

function Card({ id, name, species, gender, image, onClose, deleteFavorite, addFavorite, myFavorites }) {
   const [ isFav, setIsFav ] = useState(false)

   const { pathname } = useLocation()

   const [animate, setAnimate] = useState(false);

   const handleFavorite = () => {
      if(isFav) {
         setIsFav(false)
         deleteFavorite(id)
      }
      else {
         setIsFav(true)
         addFavorite({ id, name, species, gender, image })
      }
   }

   useEffect(() => {
      myFavorites.forEach((fav) => {
         if (fav.id === id) {
            setIsFav(true);
         }
      });
   }, [myFavorites])

   return (
      <div className={`card w-96 bg-base-100 shadow-xl ${animate ? 'animate__animated animate__bounceOut' : ''}`}>
         <figure className="px-10 pt-10">
            <img src={image} alt={name} className="rounded-xl" />
         </figure>
         <div className="card-body items-center text-center">
            <h2 className="card-title">{name}</h2>
            <p></p>
            <div className="card-actions">
               {
                  isFav ? (
                     <button onClick={handleFavorite} className="btn btn-primary">‚ù§Ô∏è</button>
                  ) 
                  : 
                  (
                     <button onClick={handleFavorite} className="btn btn-primary">ü§ç</button>
                  )
               }
            </div>
         </div>
      </div>
   );
}

const mapStateToProps = (state)=> {
   return {
      myFavorites: state.myFavorites
   }
}

const mapDispatchToProps = (dispatch) => {
   return {
      addFavorite: (character) => dispatch(addFavorite(character)),
      deleteFavorite: (id)=> dispatch((deleteFavorite(id)))
   }
}

export default connect(mapStateToProps, mapDispatchToProps)(Card)
