import React, { useEffect, useRef } from 'react';
import imageLogo from '../../assets/logoNav.png';
import eyes from '../../assets/eye.png';

const EyeSeeYou = () => {
    const eyeLeftRef = useRef(null);
    const eyeRightRef = useRef(null);

    useEffect(() => {
        const handleMouseMove = (event) => {
            [eyeLeftRef.current, eyeRightRef.current].forEach((eye) => {
                const { left, top, width, height } = eye.getBoundingClientRect();
                const eyeCenterX = left + width / 2;
                const eyeCenterY = top + height / 2;
                const radian = Math.atan2(event.pageX - eyeCenterX, event.pageY - eyeCenterY);
                const rotation = radian * (180 / Math.PI) * -1 + 180;
                eye.style.transform = `rotate(${90+angleDeg}deg)`;
            });
        };

        document.addEventListener('mousemove', handleMouseMove);

        return () => {
            document.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    return (
        <body>
            <main>
                <img id='anchor' src={imageLogo} alt="logo rick and morty" style={{ width: '250px', height: '250px', position: 'fixed', top: '0px', left: '0px' }} />
                <div id="eyes">
                    <img ref={eyeLeftRef} src={eyes} alt="left eye" style={{ position: 'absolute', width: '5px', height: '5px', top: '93px', left: '106px' }} />
                    <img ref={eyeRightRef} src={eyes} alt="right eye" style={{ position: 'absolute', width: '5px', height: '5px', top: '93px', left: '130px' }} />
                </div>
            </main>
        </body>
    )
}

export default EyeSeeYou;
